include("../src/Bioinformatics.jl")
using Bioinformatics
if VERSION < v"0.7.0-DEV.2005"
    using Base.Test
else
    using Test
end

@test verifyDna("ACGT") == true
@test_throws ErrorException verifyDna("ACFY") == false
@test countBases(readStringFromFile("../test/data/rosalind_dna.txt")) == (201, 182, 223, 195)
@test transcribeDna(readStringFromFile("../test/data/rosalind_rna.txt")) == ""
@test reverseComplement(readStringFromFile("../test/data/rosalind_revc.txt")) == ""
@test maximum(values(gcContent(readFASTA("../test/data/rosalind_gc.txt")))) == 51.125
